INSERT INTO movies (imdb_id, title, director, release_year, runtime_minutes, plot, genero) VALUES
('tt0095016', 'Cinema Paradiso', 'Giuseppe Tornatore', 1988, 155, 'Un cineaste recuerda su infancia al enamorarse de las imágenes en el cine de su pueblo natal y forma una profunda amistad con el proyeccionista del cine.', 'DRAMA'),
('tt0099685', 'GoodFellas', 'Martin Scorsese', 1990, 145.2, 'La historia de Henry Hill y su vida en la mafia, que narra su relación con su esposa Karen y sus compañeros de la mafia Jimmy Conway y Tommy DeVito en el sindicate del crimen italo-estadounidense.', 'CRIMEN'),
('tt0353969', 'Memories of Murder', 'Bong Joon Ho', 2003, 131.4, 'En una pequeña provincia coreana en 1986, tres detectives buscan al culpable de una serie de violaciones y asesinatos de mujeres jóvenes.', 'CRIMEN'),
('tt0053604', 'The Apartment', 'Billy Wilder', 1960, 125, 'Un hombre intenta ascender en su compañía dejando que sus ejecutivos usen su departamente para citas.', 'COMEDIA'),
('tt0064115', 'Butch Cassidy and the Sundance Kid', 'George Roy Hill', 1969, 110, 'Principios del siglo veinte, en Wyoming. Butch Cassidy y The Sundance KId son los líderes de una banda de forajidos. Después de que el robo a un tren fracasa, se encuentran huyendo con un pelotón que les pisa los talones. Su solución: escapar a Bolivia.', 'WESTERN'),
('tt0816692', 'Interstellar', 'Christopher Nolan', 2014, 169, 'Un grupo de exploradores prueban los saltos a través de agujeros de gusano en búsqueda de la sobrevivencia de la humanidad.', 'DRAMA'),
('tt0050083', '12 Angry Men', 'Sidney Lumet', 1957, 96, 'Doce miembros de un jurado deben juzgar a un adolescente acusado de haber matado a su padre. Todos menos uno están convencidos de la culpabilidad del acusado. El que disient intenta con sus razonamientos introducir en el debate una duda razonable que haga recapacitar a sus compañeros para que cambien el sentido de su voto.', 'JURIDICO'),
('tt0034583', 'Casablanca', 'Michael Curtiz', 1942, 102, 'El escéptico propietario de un club protege a un antiguo amor y a su marido de los nazis en Marruecos.', 'ROMANCE')